import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components'; // Import styled-components and keyframes
import sparkle from '../images/sparkle.svg'; // Import your SVG

// Default color for sparkles
const DEFAULT_COLOR = '#FFC700';

// Utility function to generate a random number between min and max
const random = (min, max) => Math.floor(Math.random() * (max - min + 1)) + min;

// Utility function to create an array with a range of numbers
const range = (count) => Array.from({ length: count }, (_, i) => i);

// Utility function to generate a vibrant color
const generateVibrantColor = () =>
    `hsl(${random(0, 360)}, 100%, ${random(50, 70)}%)`;

// Utility function to generate a sparkle object
const generateSparkle = (color) => ({
  id: String(random(10000, 99999)),
  createdAt: Date.now(),
  color: generateVibrantColor(),
  color,
  size: random(10, 20),
  style: {
    top: `${random(0, 100)}%`,
    left: `${random(0, 100)}%`,
  },
});

// Custom hook for reduced motion preference
const usePrefersReducedMotion = () => {
  const [prefersReducedMotion, setPrefersReducedMotion] = useState(false);

  useEffect(() => {
    const mediaQuery = window.matchMedia('(prefers-reduced-motion: reduce)');
    const handleChange = () => setPrefersReducedMotion(mediaQuery.matches);

    mediaQuery.addEventListener('change', handleChange);
    setPrefersReducedMotion(mediaQuery.matches);

    return () => mediaQuery.removeEventListener('change', handleChange);
  }, []);

  return prefersReducedMotion;
};

// Custom hook for random intervals
const useRandomInterval = (callback, minDelay, maxDelay) => {
  const timeoutId = React.useRef(null);

  const runInterval = () => {
    const timeout = random(minDelay, maxDelay);
    timeoutId.current = setTimeout(() => {
      callback();
      runInterval();
    }, timeout);
  };

  useEffect(() => {
    if (minDelay === null || maxDelay === null) return;

    runInterval();
    return () => clearTimeout(timeoutId.current);
  }, [minDelay, maxDelay, callback]);
};

// Sparkles component
const Sparkles = ({ color = DEFAULT_COLOR, children, ...delegated }) => {
  const [sparkles, setSparkles] = useState(() => range(3).map(() => generateSparkle(color)));
  const prefersReducedMotion = usePrefersReducedMotion();

  useRandomInterval(
    () => {
      const sparkle = generateSparkle(color);
      const now = Date.now();
      const nextSparkles = sparkles.filter((sp) => now - sp.createdAt < 750);

      nextSparkles.push(sparkle);
      setSparkles(nextSparkles);
    },
    prefersReducedMotion ? null : 50,
    prefersReducedMotion ? null : 450
  );

  return (
    <Wrapper {...delegated}>
      {sparkles.map((sparkle) => (
        <Sparkle
          key={sparkle.id}
          color={sparkle.color}
          size={sparkle.size}
          style={sparkle.style}
        />
      ))}
      <ChildWrapper>{children}</ChildWrapper>
    </Wrapper>
  );
};

// Individual Sparkle component
const Sparkle = ({ size, color, style }) => (
    <SparkleWrapper style={style}>
      <ReactSVG
        src={sparkle}
        beforeInjection={(svg) => {
          svg.setAttribute('width', size);
          svg.setAttribute('height', size);
          svg.querySelector('path').setAttribute('fill', color); // Apply dynamic color
        }}
      />
    </SparkleWrapper>
  );

// Keyframes for animations
const comeInOut = keyframes`
  0% {
    transform: scale(0);
  }
  50% {
    transform: scale(1);
  }
  100% {
    transform: scale(0);
  }
`;

const spin = keyframes`
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(180deg);
  }
`;

// Styled components
const Wrapper = styled.span`
  display: inline-block;
  position: relative;
`;

const SparkleWrapper = styled.span`
  position: absolute;
  display: block;

  @media (prefers-reduced-motion: no-preference) {
    animation: ${comeInOut} 700ms forwards;
  }
`;

const SparkleSvg = styled.img`
  display: block;

  @media (prefers-reduced-motion: no-preference) {
    animation: ${spin} 1000ms linear;
  }
`;

const ChildWrapper = styled.strong`
  position: relative;
  z-index: 1;
  font-weight: bold;
`;

export default Sparkles;
